/*
 * step rate is the number of sin table steps to advance by each pwm cycle
 * ideally this should be some sort of fixed point value that adds an extra step whenever the remainder rolls over
 * but for now it will be simply an integer number of steps each update
 * 
 * index is the time take to complete one full cycle from the sensor input, meaused over the last cycle each time the sensor input changes
 * in microseconds, measured using timer0 (which by default has 4us resolution)
 */
//dithering limits the maximum resolution of the sin table
//sin table must be less than 65536>>SIN_RATE_SCALE_BITS long

#ifndef PWM_DITHER
#define step_rate_table             step_rate_sin3840_res32us_min829us_max8319us_pp2_pwm31373hz
#else
#define step_rate_table             step_rate_sin3840x8_res32us_min829us_max8319us_pp2_pwm31373hz
#endif
 /* configuration for this table:
  *  sin table size: 3840 
  *    UPDATE_RATE_Hz  = 31250
  *    POLE_PAIRS      = 2  //number of electrical cyles per shaft revolution
  *    plus:
  */
#define  MIN_RPM       600 //minimum rpm we can expect to handle
#define  MAX_RPM       6000 //maximum rpm we can expect to handle
  
#define  STEP_RATE_MINIMUM_us     829 //offset microseconds for table index 0
#define  STEP_RATE_RES_us_LOG2    5     //log2(resoltion_us) - this should match TMR1 period

/* this is the relative resolution of the timer compared to the table */
#define STEP_RATE_RES_tck_LOG2   (STEP_RATE_RES_us_LOG2-TMR1_RES_us_LOG2)
/* step rate minimum in TMR1_tck's */
#define STEP_RATE_MINIMUM_tck     (STEP_RATE_MINIMUM_us>>TMR1_RES_us_LOG2)

//get the table index for the given interval in microseconds
#define STEP_RATE_IDX_us(interval_us) ((interval_us-STEP_RATE_MINIMUM_us)>>STEP_RATE_RES_us_LOG2)

//get the table index for the given interval in TMR1_tck's
#define STEP_RATE_IDX_tck(interval_tck) ((interval_tck-STEP_RATE_MINIMUM_tck)>>STEP_RATE_RES_tck_LOG2)
/* method to access table */
#define STEP_RATE_us(interval_us)    pgm_read_byte_near(step_rate_table + STEP_RATE_IDX_us(interval_us))
#define STEP_RATE_tck(interval_tck)    pgm_read_byte_near(step_rate_table + STEP_RATE_IDX_tck(interval_tck))
#define STEP_RATE(idx)    pgm_read_byte_near(step_rate_table + idx)

#define STEP_RATE_TABLE_SIZE    (sizeof(step_rate_table))
#define STEP_RATE_MAXIMUM_us    (STEP_RATE_MINIMUM_us+((STEP_RATE_TABLE_SIZE-1)<<STEP_RATE_RES_us_LOG2))
#define STEP_RATE_MAXIMUM_tck    (STEP_RATE_MINIMUM_tck+(STEP_RATE_TABLE_SIZE-1))

